  //   alerts.map(alert => {
  //     return ( <div>
  //       {alert.msg}
  //     </div>)
  //   });
  // }
  //
  // return <div>{descriptionAlert}</div>;
  alerts !== null &&
  alerts.length > 0 &&
  alerts.map(alert => (
    <div key={alert.id} className={`alert alert-${alert.alertType}`}>
      {alert.msg}
    </div>
  ));
};

Alert.propTypes = {
  name: PropTypes.string
};

const mapStateToProps = state => ({
  alerts: state.alert
});

export default connect(mapStateToProps)(Alert);
